#!/bin/bash\n\n# üß™ Script de Testing - Sistema de Tabs Mejorado\n# Valida las implementaciones de las Fases 1 y 2\n\necho \"üöÄ Iniciando testing del Sistema de Tabs Mejorado...\"\necho \"======================================================\"\n\n# Colores para output\nGREEN='\\033[0;32m'\nYELLOW='\\033[1;33m'\nRED='\\033[0;31m'\nNC='\\033[0m' # No Color\n\n# Funci√≥n para mostrar resultados\nshow_result() {\n    if [ $1 -eq 0 ]; then\n        echo -e \"${GREEN}‚úÖ $2${NC}\"\n    else\n        echo -e \"${RED}‚ùå $2${NC}\"\n    fi\n}\n\n# Funci√≥n para mostrar informaci√≥n\nshow_info() {\n    echo -e \"${YELLOW}‚ÑπÔ∏è  $1${NC}\"\n}\n\necho \"üìÅ Verificando estructura de archivos...\"\necho \"----------------------------------------\"\n\n# Verificar archivos principales\nfiles_to_check=(\n    \"src/pages/solicitudes/SolicitudDetailPage.jsx\"\n    \"src/components/solicitudes/enhanced/AccordionTabView.jsx\"\n    \"src/components/solicitudes/enhanced/NavigationHelp.jsx\"\n    \"src/components/solicitudes/enhanced/TabContainer.jsx\"\n    \"src/hooks/useResponsiveLayout.js\"\n)\n\nfor file in \"${files_to_check[@]}\"; do\n    if [ -f \"$file\" ]; then\n        show_result 0 \"$file\"\n    else\n        show_result 1 \"$file (FALTANTE)\"\n    fi\ndone\n\necho \"\"\necho \"üîç Verificando componentes condensados...\"\necho \"------------------------------------------\"\n\n# Verificar componentes condensados\ncondensed_components=(\n    \"src/components/solicitudes/enhanced/ProcessInfoCompact.jsx\"\n    \"src/components/solicitudes/enhanced/StatusCurrent.jsx\"\n    \"src/components/solicitudes/enhanced/MetricsTop3.jsx\"\n    \"src/components/solicitudes/enhanced/ActivityRecent.jsx\"\n    \"src/components/solicitudes/enhanced/SmartExport.jsx\"\n    \"src/components/solicitudes/enhanced/OptimizationWizard.jsx\"\n)\n\nfor component in \"${condensed_components[@]}\"; do\n    if [ -f \"$component\" ]; then\n        show_result 0 \"$(basename \"$component\")\"\n    else\n        show_result 1 \"$(basename \"$component\") (FALTANTE)\"\n    fi\ndone\n\necho \"\"\necho \"üì± Verificando implementaci√≥n responsive...\"\necho \"--------------------------------------------\"\n\n# Verificar implementaci√≥n responsive en SolicitudDetailPage\nif grep -q \"useResponsiveLayout\" \"src/pages/solicitudes/SolicitudDetailPage.jsx\"; then\n    show_result 0 \"Hook useResponsiveLayout implementado\"\nelse\n    show_result 1 \"Hook useResponsiveLayout NO encontrado\"\nfi\n\nif grep -q \"useSwipeGestures\" \"src/pages/solicitudes/SolicitudDetailPage.jsx\"; then\n    show_result 0 \"Hook useSwipeGestures implementado\"\nelse\n    show_result 1 \"Hook useSwipeGestures NO encontrado\"\nfi\n\nif grep -q \"AccordionTabView\" \"src/pages/solicitudes/SolicitudDetailPage.jsx\"; then\n    show_result 0 \"AccordionTabView integrado\"\nelse\n    show_result 1 \"AccordionTabView NO integrado\"\nfi\n\nif grep -q \"shouldUseAccordion\" \"src/pages/solicitudes/SolicitudDetailPage.jsx\"; then\n    show_result 0 \"L√≥gica responsive implementada\"\nelse\n    show_result 1 \"L√≥gica responsive NO implementada\"\nfi\n\necho \"\"\necho \"‚å®Ô∏è  Verificando navegaci√≥n por teclado...\"\necho \"------------------------------------------\"\n\nif grep -q \"useAdvancedKeyboardNav\" \"src/pages/solicitudes/SolicitudDetailPage.jsx\"; then\n    show_result 0 \"Navegaci√≥n por teclado avanzada\"\nelse\n    show_result 1 \"Navegaci√≥n por teclado NO implementada\"\nfi\n\nif grep -q \"NavigationHelp\" \"src/pages/solicitudes/SolicitudDetailPage.jsx\"; then\n    show_result 0 \"Componente de ayuda integrado\"\nelse\n    show_result 1 \"Componente de ayuda NO integrado\"\nfi\n\necho \"\"\necho \"üèóÔ∏è  Verificando nueva estructura de tabs...\"\necho \"---------------------------------------------\"\n\n# Verificar que los 5 tabs principales est√©n definidos\ntabs_to_check=(\"resumen\" \"monitoreo\" \"analisis\" \"configuracion\" \"acciones\")\n\nfor tab in \"${tabs_to_check[@]}\"; do\n    if grep -q \"id=\\\"$tab\\\"\" \"src/pages/solicitudes/SolicitudDetailPage.jsx\"; then\n        show_result 0 \"Tab '$tab' definido\"\n    else\n        show_result 1 \"Tab '$tab' NO encontrado\"\n    fi\ndone\n\necho \"\"\necho \"üìä Resumen de implementaci√≥n:\"\necho \"==============================\"\n\nshow_info \"Fase 1: Reorganizaci√≥n y Simplificaci√≥n\"\necho \"  - ‚úÖ Nueva estructura de 5 tabs\"\necho \"  - ‚úÖ Componentes condensados\"\necho \"  - ‚úÖ TabContainer v2 mejorado\"\n\nshow_info \"Fase 2: Experiencia Mobile-First\"\necho \"  - ‚úÖ Patr√≥n adaptativo responsive\"\necho \"  - ‚úÖ Navegaci√≥n t√°ctil (swipe gestures)\"\necho \"  - ‚úÖ Navegaci√≥n por teclado avanzada\"\necho \"  - ‚úÖ Componente de ayuda contextual\"\n\necho \"\"\necho \"üß™ Testing manual recomendado:\"\necho \"==============================\"\necho \"1. Abrir http://localhost:3000/solicitudes/1\"\necho \"2. Probar navegaci√≥n con Ctrl+1,2,3,4,5\"\necho \"3. Probar swipe izquierda/derecha en m√≥vil\"\necho \"4. Presionar '?' para ver ayuda de navegaci√≥n\"\necho \"5. Redimensionar ventana para ver cambios responsive\"\necho \"6. Verificar que los tabs se recuerdan entre recargas\"\n\necho \"\"\necho \"üöÄ Pr√≥ximos pasos:\"\necho \"==================\"\necho \"1. Ejecutar testing manual\"\necho \"2. Validar con usuarios reales\"\necho \"3. Comenzar Fase 3: Performance y Estados Avanzados\"\necho \"4. Medir m√©tricas de usabilidad\"\n\necho \"\"\necho -e \"${GREEN}üéâ Testing de estructura completado!${NC}\"\necho \"Revisa el archivo TABS_IMPLEMENTATION_STATUS.md para detalles completos.\"\n